import React, {useState, useEffect} from 'react';
import {Button} from "./Componet-style";
import AccountSchema from './Schema/AccountSchema'
// import axios from "axios";

// import * as yup from "yup"

const Account = (props) => {




    // to keep track of your input

    const [info, setInfo] = useState({
        name: '',
        lastName: "",
        //////////
        email: "",
        password: '',
        terms: false,
        ///////

    })

    // to keep track of our errors

    const [errors, setErrors] = useState({
        name: '',
        lastName: "",
        //////////
        email: "",
        password: '',
        terms: "",
        ///////

    })

    const [disabled, setDisabled] = useState(true)

useEffect(()=>AccountSchema.isValid(info).then(valid => {
    setDisabled(!valid)
})[info])



    const changeinput = (event) => {
        // event.persist()   /// add this after seting up the error validation
        const {value, name, type, checked} = event.target
        const newValue = type === "checkbox" ? checked : value
        // validation(name, newValue)   // add this after persist is added
        setInfo({...info, [name]: newValue})
    }



    const submit = (event) => {
        event.preventDefault()


    }


    return (
        <form onSubmit={submit}>
            <label htmlFor={'name'}> Name:
                <input
                    data-cy={'name'}
                    id={'name'}
                    name="name"
                    onChange={changeinput}
                    value={info.name}
                    placeholder={'write your name'}
                />

                {/*after yup is fully finished */}

                {/*{errors.name.length > 0 ? <p className={"error"}>{errors.name}</p> : null}*/}

            </label>
            <label htmlFor={"lastName"}> Last Name:
                <input
                    data-cy={"lastName"}
                    name={'lastName'}
                    onChange={changeinput}
                    value={info.lastName}
                    placeholder={'tell your your last name'}
                />
                {/*{errors.lastName.length > 0 ? <p className={"error"}>{errors.lastName}</p> : null}*/}
            </label>

            {/*/////////////////////////////////////////////////// email password and checkbox   //////////////////////////*/}

            <label htmlFor={'email'}> Email
                <input
                    data-cy={"email"}
                    id={'email'}
                    name="email"
                    onChange={changeinput}
                    value={info.email}
                    placeholder={'Enter your email'}
                    type="email"
                />
                {/*{errors.email.length > 0 ? <p className={"error"}>{errors.email}</p> : null}*/}
            </label>

            <label htmlFor={'password'}> Password
                <input
                    data-cy={"password"}
                    id={'password'}
                    name="password"
                    onChange={changeinput}
                    value={info.password}
                    type={"password"} placeholder={"Enter your password"}
                />
                {/*{errors.password.length > 0 ? <p className={"error"}>{errors.password}</p> : null}*/}
            </label>

            <label htmlFor={'terms'}> Terms and Conditions

                <input
                    data-cy={"terms"}
                    type="checkbox"
                    name="terms"
                    checked={info.terms}
                    value={info.terms}
                    onChange={changeinput}
                />
                {/*{errors.terms.length > 0 ? <p className={"error"}>{errors.terms}</p> : null}*/}
            </label>


            {/*/////////////////////////////////////////////////// end  ///////////////////////////////////////////////////*/}

            {/*<pre className={'error'}>{JSON.stringify(postError, null, 2)}</pre>*/}
            {/*// to show the error from error .catch i  axios*/}

            <button  data-cy={"submit"} disabled={disabled}>submit</button>


        </form>
    )


}
export default Account
